example.programlisting:
{* we don't want to print a table row unless content is displayed *}
{capture name=banner}
  {include file='get_banner.tpl'}
{/capture}

{if $smarty.capture.banner ne ''}
<table>
<tr>
  <td>
   {$smarty.capture.banner}
  </td>
</tr>
</table>
{/if}


{* we don't want to print a table row unless content is displayed *}
{capture name=banner assign=foo}
  .... some content ....
{/capture}

{if $condition}
{$foo}
{/if}
